'use client';

import styled from 'styled-components';
import Image from 'next/image';
import { CustomNavigator } from '@/customComponents/CustomNavigator';
import { Breakpoints } from '@/constants/layoutConstants';
import { FiDownload } from 'react-icons/fi';
import { AppColors } from '@/styles/colors';
import CommonButton from '@/components/CommonButton';

interface AIBlockProps {
  topLabel: string;
  centerLabel: string;
  bottomLabel: string;
  title: string;
  description: string;
  sectionTitle: string;
  sectionDescription: string;
  imageUrl: string;
  buttonText?: string;
  buttonHeader: string;
  buttonDescription: string;
  onButtonClick?: () => void;
  onTopArrowClick?: () => void;
  onBottomArrowClick?: () => void;
}

const withLineBreaks = (text: string) =>
  text.split('\n').map((line, index) => (
    <span key={index}>
      {line}
      <br />
    </span>
  ));

const SectionWrapper = styled.div`
  padding: 0 20px;
  box-sizing: border-box;
  min-width: ${Breakpoints.desktop}px;

  @media (max-width: ${Breakpoints.mobile}px) {
    min-width: auto;
  }
`;

const Container = styled.div`
  max-width: ${Breakpoints.desktop}px;
  display: flex;
  justify-content: space-between;
  background: #1c1c2b;
  align-items: center;
  gap: 40px;
  padding: 40px 20px;
  box-sizing: border-box;
  border-radius: 16px;

  @media (max-width: ${Breakpoints.mobile}px) {
    flex-direction: column;
    align-items: flex-start;
  }
`;

const LeftImageBlock = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;

  img {
    width: 540px;
    height: 1032px;
    object-fit: contain;

    @media (max-width: ${Breakpoints.mobile}px) {
      width: 100%;
      height: auto;
    }
  }
`;

const RightTextBlock = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

const Heading = styled.h3`
  font-size: 30px;
  font-weight: 700;
  color: white;
  margin-bottom: 0px;

  @media (max-width: ${Breakpoints.mobile}px) {
    font-size: 20px;
    line-height: 1.4;
  }
`;

const SubText = styled.p`
  font-size: 20px;
  font-weight: 500;
  color: #ccc;
  margin-bottom: 24px;
  line-height: 1.6;

  @media (max-width: ${Breakpoints.mobile}px) {
    font-size: 14px;
    line-height: 1.4;
  }
`;

const DescriptionText = styled.p`
  font-size: 18px;
  font-weight: 600;
  color: white;
  margin-top: 24px;

  @media (max-width: ${Breakpoints.mobile}px) {
    font-size: 14px;
  }
`;

export const AIBlock: React.FC<AIBlockProps> = ({
  topLabel,
  centerLabel,
  bottomLabel,
  title,
  description,
  sectionTitle,
  sectionDescription,
  imageUrl,
  buttonText = 'Go to AI Quote',
  buttonHeader,
  buttonDescription,
  onButtonClick,
  onTopArrowClick,
  onBottomArrowClick,
}) => {
  return (
    <>
      <CustomNavigator
        topLabel={topLabel}
        centerLabel={centerLabel}
        bottomLabel={bottomLabel}
        title={title}
        description={description}
        onTopArrowClick={onTopArrowClick}
        onBottomArrowClick={onBottomArrowClick}
      />

      <SectionWrapper>
        <Container>
          <LeftImageBlock>
            <Image
              src="/assets/phone_frame.svg"
              alt="AI 견적 이미지"
              width={540}
              height={1032}
            />
          </LeftImageBlock>

          <RightTextBlock>
            <CommonButton
              text={buttonText}
              backgroundColor={AppColors.background}
              borderRadius="75px"
              icon={<FiDownload />}
              $iconPosition="left"
              onClick={onButtonClick}
            />
            <DescriptionText>{buttonHeader}</DescriptionText>
            <DescriptionText>{buttonDescription}</DescriptionText>
          </RightTextBlock>
        </Container>
      </SectionWrapper>
    </>
  );
};
