+import React, { useEffect, useRef } from 'react';
+import gsap from 'gsap';
+import ScrollTrigger from 'gsap/ScrollTrigger';
+
+const colors = ['#FF6B6B', '#4ECDC4', '#FFD93D'];
+const containerHeight = 600;
+const gap = 100;
+const maxZIndex = 100;
+
+gsap.registerPlugin(ScrollTrigger);
+
+const Container3DStackScroll: React.FC = () => {
+  const boxRefs = useRef<(HTMLDivElement | null)[]>([]);
+
+  useEffect(() => {
+    const triggers: ScrollTrigger[] = [];
+
+    boxRefs.current.forEach((el, i) => {
+      if (!el) return;
+
+      triggers.push(
+        ScrollTrigger.create({
+          trigger: el,
+          start: `top-=${i * gap} top`, // ✅ 각기 다른 start 지점
+          pin: true,
+          pinSpacing: false,
+          anticipatePin: 1,
+        })
+      );
+    });
+
+    return () => {
+      triggers.forEach(trigger => trigger.kill());
+    };
+  }, []);
+
+  return (
+    <div style={{ height: `${colors.length * (containerHeight + gap)}px`, position: 'relative' }}>
+      {colors.map((color, index) => (
+        <div
+          key={index}
+          ref={(el) => {
+            boxRefs.current[index] = el;
+          }}
+          style={{
+            position: 'relative',
+            width: '100%',
+            height: `${containerHeight}px`,
+            backgroundColor: color,
+            zIndex: maxZIndex - index, // 위에서부터 덮어야 하므로 큰 숫자부터
+            display: 'flex',
+            alignItems: 'center',
+            justifyContent: 'center',
+            fontSize: '32px',
+            color: 'white',
+            fontWeight: 'bold',
+          }}
+        >
+          Box {index + 1}
+        </div>
+      ))}
+    </div>
+  );
+};
+
+export default Container3DStackScroll;

